create table dht_read (heat_index float4 not null, humidity float4 not null, temperature float4 not null, created_at timestamp(6) with time zone not null, id bigint generated by default as identity, updated_at timestamp(6) with time zone not null, primary key (id));
create table gpio (pin smallint not null unique, state smallint not null check (state between 0 and 2), type smallint not null check (type between 0 and 1), created_at timestamp(6) with time zone not null, id bigint generated by default as identity, updated_at timestamp(6) with time zone not null, primary key (id));
create table users (created_at timestamp(6) with time zone not null, id bigint generated by default as identity, updated_at timestamp(6) with time zone not null, password varchar(255) not null, username varchar(255) not null unique, primary key (id));

